    @Test
    void shouldReturnCountriesInDescForSortedPd(){
        Mockito.when(service.getCountries()).thenReturn(countries);
        assertTrue(controller.getPdSortedObjects().getBody().get(0).getPopulationDensity()>=controller.getPdSortedObjects().getBody().get(1).getPopulationDensity());
    }

    @Test
    void shouldReturnAllCountriesForSortedPd(){
        Mockito.when(service.getCountries()).thenReturn(countries);
        List<CountryResponseBody> result = controller.getPdSortedObjects().getBody();
        assertEquals(countries.length,result.size());
    }

    @Test
    void shouldReturnNotFoundForSortedPdEmpty(){
        Mockito.when(service.getCountries()).thenReturn(null);
        assertEquals(HttpStatus.NOT_FOUND,controller.getPdSortedObjects().getStatusCode());
    }

    @Test
    void shouldReturnMaxDiffRegionBorder(){
        Mockito.when(service.getCountries()).thenReturn(countries);
        assertNotNull(controller.filterByMaxRegion());
    }

    @Test
    void shouldReturnErrorMaxDiffRegionBorderEmpty(){
        Mockito.when(service.getCountries()).thenReturn(null);
        assertEquals(HttpStatus.NOT_FOUND,controller.filterByMaxRegion().getStatusCode());
    }

